fs = require('fs');

module.exports = {
    read: function(name, callback) {
        fs.readFile(name, 'utf-8', function(err, data) {
            if (err) return callback(err);
            callback(null, data);
        });
    },
    create: function(name, callback) {
        fs.writeFile(name, 'utf-8', function(err, data) {
            if (err) return callback('Create ' + name + ' : KO');
            callback(null, 'Create ' + name + ' : OK');
        });
    },
    update: function(name, update, callback) {
        fs.writeFile(name, update, 'utf-8', function(err, data) {
            if (err) return callback('Update ' + name + ' : KO');
            callback(null, 'Update ' + name + ' : OK');
        });
    },
    delete: function(name, callback) {
        fs.unlink(name, function(err, data) {
            if (err) return callback('Delete ' + name + ' : KO');
            callback(null, 'Delete ' + name + ' : OK');
        });
    }
}