import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch,
  useParams
} from "react-router-dom";


class SubComponent extends React.Component {
  render() {
    return (
      <h2>{this.props.nom}</h2>
  )
  }
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      produits: [],
    }
  }

  componentDidMount() {
    axios.get("http://localhost:5000/express_backend").then((data) => {
      this.setState({ produits: data.data.produits });

    })
  } 

  render() {
    return (

      <div className="App">
        <Router>
      <div>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/topics">Topics</Link>
          </li>
        </ul>

        <Switch>
          <Route path="/about">
            <new About />
          </Route>
          <Route path="/topics">
            <new Topics />eeee
          </Route>
          <Route path="/">
            <new Home />
          </Route>
        </Switch>
      </div>
        </Router>


        {/* <div>{this.state.produits.map((e) => {
          return (
            <SubComponent nom={e.nom}/>
         )
       })}</div> */}
      </div>
    );
  }
}

class Home {
  render() {return(<h2> Home</h2 >);}
}

class About {
 render() {return(<h2> About</h2 >);}
}

class Topics {
 render() {return(<h2> Topics</h2 >);}
}

export default App;
